import React, { useState, useEffect } from "react";
import axios from "axios";
import Modal from "react-bootstrap/Modal";
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";

export default function AddUser(props) {
  const [show, setShow] = useState(false);

  const handleClose = () => setShow(false);
  const handleShow = () => setShow(true);

  const [name, setName] = useState("");
  const [address, setAddress] = useState("");
  const [startDate, setStartDate] = useState(new Date());
  const [gender, setGender] = useState("");
  const [college, setCollege] = useState("");
  const [hobbies, setHobbies] = useState("");
  const today = new Date();

  const getCollegeNames = async (searchTerm) => {
    const response = await axios.get(
      `http://universities.hipolabs.com/search?name=${searchTerm}`
    );
  };

  const handleSearchInput = async () => {
    getCollegeNames(searchTerm);
  };

  // submit form
  const submitHandler = async (e) => {
    e.preventDefault();
  };

  useEffect(() => {}, []);

  // set date
  const selectDateHandler = (d) => {
    setStartDate(d);
  };

  const handleClickOther = () => {
    setShow(true);
  };

  return (
    <>
      <div className="row">
        {/* <Modal open={props.addUser} toggleModal={props.handleModalToggle}>
        <Modal.Body> */}
        <form className="form" onSubmit={submitHandler}>
          <div>
            <label htmlFor="name">Name</label>
            <input
              type="name"
              id="name"
              placeholder="Enter Name"
              required
              onChange={(e) => setName(e.target.value)}
            />
          </div>
          <div>
            <label htmlFor="birthdate">Birth Date</label>
            <DatePicker
              dateFormat="yyyy/MM/dd"
              selected={startDate}
              onChange={selectDateHandler}
              minDate={today}
              todayButton={"Today"}
            />
          </div>
          <div>
            <label htmlFor="address">Address</label>
            <input
              type="address"
              id="address"
              placeholder="Enter Address"
              required
              onChange={(e) => setAddress(e.target.value)}
            />
          </div>
          <div>
            <label htmlFor="gender">Gender</label>
            <input
              type="gender"
              id="gender"
              placeholder="Enter Gender"
              required
              onChange={(e) => setGender(e.target.value)}
            />
          </div>
          <div>
            <label htmlFor="college">College</label>
            <input
              type="college"
              id="college"
              placeholder="Enter college"
              required
              onChange={(e) => setCollege(e.target.value)}
            />
          </div>
          <div>
            <label htmlFor="college">Hobbies</label>
            <div className="row">
              <div>
                <input
                  type="checkbox"
                  id="reading"
                  value="Reading"
                  name="reading"
                  required
                  onChange={(e) => setHobbies(e.target.value)}
                />
                <label htmlFor="reading">Reading</label>
              </div>
              <div>
                <input
                  type="checkbox"
                  id="gaming"
                  value="Gaming"
                  name="gaming"
                  required
                  onChange={(e) => setHobbies(e.target.value)}
                />
                <label htmlFor="gaming">Gaming</label>
              </div>
              <div>
                <input
                  type="checkbox"
                  id="travelling"
                  value="Travelling"
                  name="travelling"
                  required
                  onChange={(e) => setHobbies(e.target.value)}
                />
                <label htmlFor="travelling">Travelling</label>
              </div>
              <div>
                <input
                  type="checkbox"
                  id="drawing"
                  value="Drawing"
                  name="drawing"
                  required
                  onChange={(e) => setHobbies(e.target.value)}
                />
                <label htmlFor="drawing">Drawing</label>
              </div>

              <div>
                <input
                  type="checkbox"
                  id="others"
                  value="Others"
                  name="others"
                  required
                  onChange={(e) => setHobbies(e.target.value)}
                  onClick={handleClickOther}
                />
                <label htmlFor="others">Others</label>
              </div>
              {show && (
                <div>
                  <textarea
                    placeholder="Enter your hobby here"
                    // value={}0
                    onChange={(e) => setHobbies(e.target.value)}
                  />
                </div>
              )}
            </div>
          </div>
          <div>
            <label />
            <button className="primary" type="submit">
              {" "}
              Submit
            </button>
          </div>
        </form>
      </div>
      {/* </Modal.Body>
      </Modal> */}
    </>
  );
}
