import React, { useState, useEffect } from "react";
import axios from "axios";
import fs from "fs";
import { nanoid } from "nanoid";
import Modal from "react-bootstrap/Modal";
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";
import { update } from "../actions/userActions";
import { useDispatch, useSelector } from "react-redux";

export default function EditUser(props) {
  console.log("props-----===>>>>>", props);

  const [show, setShow] = useState(false);
  const [searchTerm, setSearchTerm] = useState(props.dataEdit.college);
  const [collegeList, setCollegeList] = useState([]);
  const [checked, setChecked] = useState(false);

  const [name, setName] = useState(props.dataEdit.name);
  const [address, setAddress] = useState(props.dataEdit.address);
  // const [startDate, setStartDate] = useState(props.dataEdit.birthDate);
  const [gender, setGender] = useState(props.dataEdit.gender);
  const [hobbies, setHobbies] = useState(props.dataEdit.hobbies);
  const [otherHobby, setOtherHobby] = useState(props.dataEdit.otherHobby);
  // let reading_flag = "";
  // let gaming_flag = "";
  // let travelling_flag = "";
  // let drawing_flag = "";
  // let Others_flag = "";

  // // states to update hobbies

  // useEffect(() => {
  //   const hobbiesFlag = () => {
  //     reading_flag = hobbies.includes("Reading") ? true : false;
  //     gaming_flag = hobbies.includes("Gaming") ? true : false;
  //     travelling_flag = hobbies.includes("Travelling") ? true : false;
  //     drawing_flag = hobbies.includes("Drawing") ? true : false;
  //     Others_flag = otherHobby ? true : false;
  //   };
  // }, [hobbies, otherHobby]);

  const dispatch = useDispatch();

  const userDetails = useSelector((state) => state.userDetails);
  const { userDetailsV2 } = userDetails;

  // submit form
  const handleSubmit = (e) => {
    e.preventDefault();

    console.log("hobbies-------------->>>>>>>>", hobbies);
    console.log("otherHobby-------------->>>>>>>>", otherHobby);
    if (otherHobby) {
      hobbies.push(otherHobby);
    }

    // console.log("userID ---> ", userId);
    // const userId = nanoid();
    const userData = {
      userId: props.dataEdit.userId,
      name: name,
      address: address,
      // birthDate: startDate,
      gender: gender,
      college: searchTerm,
      hobbies: hobbies,
    };

    console.log("userData----->", userData);

    dispatch(update(userData));
  };

  // useEffect(() => {
  //   if (userDetailsV2) {
  //     props.history.push("/");
  //   }
  // }, [props.history, userDetailsV2]);

  // collect hobbies
  const selectedHobbies = (e) => {
    console.log("e------------hobby--->", e);
    if (!hobbies.includes(e)) {
      hobbies.push(e);
    } else {
      hobbies.pop(e);
    }
    console.log("hobbies", hobbies);
    setHobbies(hobbies);

    let reading_flag = hobbies.includes("Reading") ? true : false;
  };

  const selectedOtherHobby = (e) => {
    setOtherHobby(e);
  };

  const getCollegeNames = async (searchTerm) => {
    if (searchTerm && searchTerm.length > 1) {
      const response = await axios.get(
        `http://universities.hipolabs.com/search?name=${searchTerm}`
      );

      console.log("response", response);
      setCollegeList(response.data);
    }
  };

  useEffect(() => {
    getCollegeNames();
  }, []);

  const handleChange = (e, type) => {
    console.log("e---->", e.target.value);
    if (type === "college") {
      setSearchTerm(e.target.value);
      getCollegeNames(searchTerm);
    } else if (type === "name") {
      setName(e.target.value);
    } else if (type === "address") {
      setAddress(e.target.value);
    } else if (type === "gender") {
      setGender(e.target.value);
    }
  };

  const handleSelectedCollege = (name) => {
    console.log("name", name);
    setSearchTerm(name);
    setCollegeList([]);
  };

  // set date
  // const selectDateHandler = (d) => {
  //   setStartDate(d);
  // };

  const handleClickOther = () => {
    setShow(true);
  };

  return (
    <>
      <div className="row">
        {/* <Modal open={props.addUser} toggleModal={props.handleModalToggle}>
          <Modal.Body> */}
        <form className="form" onSubmit={handleSubmit}>
          <div>
            <label htmlFor="name">Name</label>
            <input
              type="name"
              id="name"
              placeholder="Enter Name"
              required
              value={name}
              onChange={(e) => handleChange(e, "name")}
            />
          </div>
          <div>
            <label htmlFor="birthdate">Birth Date</label>
            {/* <DatePicker
              dateFormat="yyyy/MM/dd"
              selected={startDate}
              onChange={selectDateHandler}
              todayButton={"Today"}
              // maxDate={today}
            /> */}
          </div>
          <div>
            <label htmlFor="address">Address</label>
            <input
              type="address"
              id="address"
              placeholder="Enter Address"
              required
              value={address}
              onChange={(e) => handleChange(e, "address")}
            />
          </div>
          <div>
            <label htmlFor="gender">Gender</label>
            <input
              type="gender"
              id="gender"
              placeholder="Enter Gender"
              required
              value={gender}
              onChange={(e) => handleChange(e, "gender")}
            />
          </div>
          <div>
            <label htmlFor="college">College</label>
            <input
              type="text"
              id="college"
              value={searchTerm}
              placeholder="search min 3 characters"
              required
              onChange={(e) => handleChange(e, "college")}
            />
          </div>
          <div>
            <div>
              {searchTerm.length > 0 && collegeList.length > 0 && (
                <div>
                  {collegeList.map((data, i) => (
                    <div
                      key={i}
                      onClick={() => handleSelectedCollege(data.name)}
                      onKeyPress={(e) => {
                        handleSelectedCollege(data.name);
                      }}
                    >
                      <div>{data.name}</div>
                    </div>
                  ))}
                </div>
              )}
            </div>
          </div>
          <div>
            <label htmlFor="hobbies">Hobbies</label>
            <div className="row">
              <div>
                <input
                  type="checkbox"
                  id="reading"
                  value="Reading"
                  name="reading"
                  checked={reading_flag}
                  onChange={(e) => selectedHobbies(e.target.value)}
                />
                <label htmlFor="reading">Reading</label>
              </div>
              <div>
                <input
                  type="checkbox"
                  id="gaming"
                  value="Gaming"
                  name="gaming"
                  checked={hobbies.includes("Gaming") ? true : false}
                  onChange={(e) => selectedHobbies(e.target.value)}
                />
                <label htmlFor="gaming">Gaming</label>
              </div>
              <div>
                <input
                  type="checkbox"
                  id="travelling"
                  value="Travelling"
                  name="travelling"
                  checked={hobbies.includes("Travelling") ? true : false}
                  onChange={(e) => selectedHobbies(e.target.value)}
                />
                <label htmlFor="travelling">Travelling</label>
              </div>
              <div>
                <input
                  type="checkbox"
                  id="drawing"
                  value="Drawing"
                  name="drawing"
                  checked={hobbies.includes("Drawing") ? true : false}
                  onChange={(e) => selectedHobbies(e.target.value)}
                />
                <label htmlFor="drawing">Drawing</label>
              </div>

              <div>
                <input
                  type="checkbox"
                  id="others"
                  value="Others"
                  name="others"
                  checked={otherHobby ? true : false}
                  // onChange={(e) => selectedHobbies(e.target.value)}
                  onClick={handleClickOther}
                />
                <label htmlFor="others">Others</label>
              </div>
              {otherHobby && (
                <div>
                  <textarea
                    placeholder="Enter your hobby here"
                    value={otherHobby}
                    onChange={(e) => selectedOtherHobby(e.target.value)}
                  />
                </div>
              )}
            </div>
          </div>
          <div>
            <label />
            <button className="primary" type="submit">
              {" "}
              Save
            </button>
          </div>
        </form>
        {/* </Modal.Body>
        </Modal> */}
      </div>
    </>
  );
}
