import {
  USER_DETAILS_FAIL,
  USER_DETAILS_REQUEST,
  USER_DETAILS_SUCCESS,
  USER_DETAILS_UPDATE_FAIL,
  USER_DETAILS_UPDATE_REQUEST,
  USER_DETAILS_UPDATE_RESET,
  USER_DETAILS_UPDATE_SUCCESS,
  USER_REMOVE,
} from "../constants/userConstants";

export const userDetailsReducer = (state = {}, action) => {
  switch (action.type) {
    case USER_DETAILS_REQUEST:
      return { loading: true };
    case USER_DETAILS_SUCCESS:
      return { loading: false, userInfo: action.payload };
    case USER_DETAILS_FAIL:
      return { loading: false, error: action.payload };
    default:
      return state;
  }
};

export const userUpdateReducer = (state = {}, action) => {
  switch (action.type) {
    case USER_DETAILS_UPDATE_REQUEST:
      return { loading: true };
    case USER_DETAILS_UPDATE_SUCCESS:
      return { loading: false, success: true };
    case USER_DETAILS_UPDATE_FAIL:
      return { loading: false, error: action.payload };
    case USER_DETAILS_UPDATE_RESET:
      return {};
    default:
      return state;
  }
};

export const userDeleteReducer = (state = { userDetailsV3: [] }, action) => {
  switch (action.type) {
    case USER_REMOVE:
      return {
        ...state,
        // userDetailsV3: state.user.filter(
        //   (user, index) => index !== action.payload
        // ),
        userDetailsV3: state.userDetailsV3.filter(
          (user) => user !== action.payload
        ),
      };

    default:
      return state;
  }
};
