import React, { useState, useEffect } from "react";
import axios from "axios";
import fs from "fs";
import { nanoid } from "nanoid";
import Modal from "react-bootstrap/Modal";
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";

export default function AddUser(props) {
  const [show, setShow] = useState(false);
  const [searchTerm, setSearchTerm] = useState("");
  const [collegeList, setCollegeList] = useState([]);

  const [name, setName] = useState("");
  const [address, setAddress] = useState("");
  const [startDate, setStartDate] = useState(new Date());
  const [gender, setGender] = useState("");
  const [hobbies, setHobbies] = useState([]);

  const today = new Date();

  // submit form
  const handleSubmit = (e) => {
    e.preventDefault();
    const userId = nanoid();
    const userData = {
      userId: {
        name: name,
        address: address,
        birthDate: startDate,
        gender: gender,
        college: searchTerm,
        hobbies: hobbies,
      },
    };
    console.log("userData----->", userData);

    // fs.writeFile("./userData.json", JSON.stringify(userData), (err) => {
    //   if (err) console.log("Error writing file:", err);
    // });
  };

  const getCollegeNames = async (searchTerm) => {
    if (searchTerm && searchTerm.length > 1) {
      const response = await axios.get(
        `http://universities.hipolabs.com/search?name=${searchTerm}`
      );

      console.log("response", response);
      setCollegeList(response.data);
    }
  };

  useEffect(() => {
    getCollegeNames();
  }, []);

  // const handleSearchInput = async () => {
  //   getCollegeNames(searchTerm);
  // };

  const handleChange = (e) => {
    console.log("e---->", e.target.value);
    setSearchTerm(e.target.value);
    getCollegeNames(searchTerm);
  };

  const handleSelectedCollege = (name) => {
    console.log("name", name);
    setSearchTerm(name);
    setCollegeList([]);
  };

  // set date
  const selectDateHandler = (d) => {
    setStartDate(d);
  };

  const handleClickOther = () => {
    setShow(true);
  };

  return (
    <>
      <div className="row">
        {/* <Modal open={props.addUser} toggleModal={props.handleModalToggle}>
        <Modal.Body> */}
        <form className="form" onSubmit={handleSubmit}>
          <div>
            <label htmlFor="name">Name</label>
            <input
              type="name"
              id="name"
              placeholder="Enter Name"
              required
              onChange={(e) => setName(e.target.value)}
            />
          </div>
          <div>
            <label htmlFor="birthdate">Birth Date</label>
            <DatePicker
              dateFormat="yyyy/MM/dd"
              selected={startDate}
              onChange={selectDateHandler}
              minDate={today}
              todayButton={"Today"}
            />
          </div>
          <div>
            <label htmlFor="address">Address</label>
            <input
              type="address"
              id="address"
              placeholder="Enter Address"
              required
              onChange={(e) => setAddress(e.target.value)}
            />
          </div>
          <div>
            <label htmlFor="gender">Gender</label>
            <input
              type="gender"
              id="gender"
              placeholder="Enter Gender"
              required
              onChange={(e) => setGender(e.target.value)}
            />
          </div>
          <div>
            <label htmlFor="college">College</label>
            <input
              type="text"
              id="college"
              value={searchTerm}
              placeholder="search min 3 characters"
              required
              onChange={handleChange}
            />
          </div>
          <div>
            <div>
              {searchTerm.length > 0 && collegeList.length > 0 && (
                <div>
                  {collegeList.map((data, i) => (
                    <div
                      key={i}
                      onClick={() => handleSelectedCollege(data.name)}
                      onKeyPress={(e) => {
                        handleSelectedCollege(data.name);
                      }}
                    >
                      <div>{data.name}</div>
                    </div>
                  ))}
                </div>
              )}
            </div>
          </div>
          <div>
            <label htmlFor="college">Hobbies</label>
            <div className="row">
              <div>
                <input
                  type="checkbox"
                  id="reading"
                  value="Reading"
                  name="reading"
                  required
                  onChange={(e) => setHobbies(e.target.value)}
                />
                <label htmlFor="reading">Reading</label>
              </div>
              <div>
                <input
                  type="checkbox"
                  id="gaming"
                  value="Gaming"
                  name="gaming"
                  required
                  onChange={(e) => setHobbies(e.target.value)}
                />
                <label htmlFor="gaming">Gaming</label>
              </div>
              <div>
                <input
                  type="checkbox"
                  id="travelling"
                  value="Travelling"
                  name="travelling"
                  required
                  onChange={(e) => setHobbies(e.target.value)}
                />
                <label htmlFor="travelling">Travelling</label>
              </div>
              <div>
                <input
                  type="checkbox"
                  id="drawing"
                  value="Drawing"
                  name="drawing"
                  required
                  onChange={(e) => setHobbies(e.target.value)}
                />
                <label htmlFor="drawing">Drawing</label>
              </div>

              <div>
                <input
                  type="checkbox"
                  id="others"
                  value="Others"
                  name="others"
                  required
                  onChange={(e) => setHobbies(e.target.value)}
                  onClick={handleClickOther}
                />
                <label htmlFor="others">Others</label>
              </div>
              {show && (
                <div>
                  <textarea
                    placeholder="Enter your hobby here"
                    // value={}0
                    onChange={(e) => setHobbies(e.target.value)}
                  />
                </div>
              )}
            </div>
          </div>
          <div>
            <label />
            <button className="primary" type="submit">
              {" "}
              Submit
            </button>
          </div>
        </form>
      </div>
      {/* </Modal.Body>
      </Modal> */}
    </>
  );
}
