import React, { useState } from "react";
import Button from "react-bootstrap/Button";
import Table from "react-bootstrap/Table";
import Modal from "react-bootstrap/Modal";
import { useSelector } from "react-redux";
import AddUser from "./AddUser";
import EditUser from "./EditUser";

export default function UserList() {
  const [addUser, setAddUser] = useState(false);
  const [editUser, setEditUser] = useState(false);
  const [deleteUser, setDeleteUser] = useState(false);
  const [dataEdit, setDataEdit] = useState("");
  const [showModal, setShowModal] = useState(false);

  // to add user
  const handleAddUser = () => {
    setAddUser(true);
  };

  // to edit
  const handleEditUser = (data) => {
    console.log("data----------->>", data);
    setEditUser(true);
    setDataEdit(data);
  };

  // to delete
  const handleDeleteUser = () => {
    setDeleteUser(true);
  };

  // toggle modal
  const handleModalToggle = () => {
    setShowModal(!showModal);
    setAddUser(false);
  };

  const userDetails = useSelector((state) => state.userDetails);
  const { userDetailsV3 } = userDetails;
  console.log("userDetailsV3-----------------------------", userDetailsV3);
  return (
    <>
      {/* {addUser ? (
        <AddUser />
      ) : ( */}
      <div>
        <div>
          <Button variant="primary" type="submit" onClick={handleAddUser}>
            Add User
          </Button>
        </div>
        {userDetailsV3 && (
          <Table striped bordered hover>
            <thead>
              <tr>
                <th>Name</th>
                <th>Address</th>
                <th>Birth Date</th>
                <th>Gender</th>
                <th>College</th>
                <th>Hobbies</th>
                <th>Action</th>
              </tr>
            </thead>
            <tbody>
              {userDetailsV3 &&
                userDetailsV3.map((data) => (
                  <tr key={data.userId}>
                    <td>{data.name}</td>
                    <td>{data.address}</td>
                    <td>{data.birthDate}</td>
                    <td>{data.gender}</td>
                    <td>{data.college}</td>
                    <td>{data.hobbies.toString().replace("Others", "")}</td>
                    <td>
                      <Button
                        variant="primary"
                        type="submit"
                        onClick={() => handleEditUser(data)}
                      >
                        Edit
                      </Button>{" "}
                      <Button
                        variant="primary"
                        type="submit"
                        onClick={handleDeleteUser}
                      >
                        Delete
                      </Button>
                    </td>
                  </tr>
                ))}
            </tbody>
          </Table>
        )}
      </div>
      {/* // )} */}
      {addUser && (
        <AddUser
          handleModalToggle={handleModalToggle}
          addUser={addUser}
          // disableBackdropToggle={true}
        />
      )}
      {editUser && <EditUser editUser={editUser} dataEdit={dataEdit} />}

      {deleteUser && (
        <Modal.Dialog>
          <Modal.Body>
            <p>Are you sure, you want to delete this user?</p>
          </Modal.Body>

          <Modal.Footer>
            <Button variant="secondary">Ok</Button>
            <Button variant="primary">Cancel</Button>
          </Modal.Footer>
        </Modal.Dialog>
      )}
    </>
  );
}
