      <div>
        <div>
          <Button variant="primary" type="submit" onClick={showModalHandler}>
            Add User
          </Button>
        </div>
        <Table striped bordered hover>
          <thead>
            <tr>
              <th>Name</th>
              <th>Address</th>
              <th>Birth Date</th>
              <th>Gender</th>
              <th>College</th>
              <th>Hobbies</th>
              <th>Action</th>
            </tr>
          </thead>
          <tbody>
            <tr>
              <td></td>
              <td></td>
              <td></td>
              <td></td>
              <td></td>
              <td></td>
              <td></td>
            </tr>
          </tbody>
        </Table>
      </div>
      <AddUser showModal={showModal} hideModalHandler={hideModalHandler} />
      {/* {addUser && <AddUser />} */}
      {/* )} */}
    </>
  );
}
