import {
  USER_DETAILS_FAIL,
  USER_DETAILS_REQUEST,
  USER_DETAILS_SUCCESS,
  USER_DETAILS_UPDATE_REQUEST,
  USER_DETAILS_UPDATE_SUCCESS,
} from "../constants/userConstants";

export const details = (userData) => async (dispatch) => {
  dispatch({
    type: USER_DETAILS_REQUEST,
    payload: userData,
  });
  try {
    dispatch({ type: USER_DETAILS_SUCCESS, payload: userData });

    // const userObj = {};
    // userObj[userData["userId"]] = userData;
    // console.log("userObj ----> ", userObj);
    let existingUsersData =
      JSON.parse(localStorage.getItem("userDetailsV2")) || [];
    console.log("existingUsersData ---> ", existingUsersData);
    existingUsersData.push(userData);
    console.log("updatedObject ---> ", existingUsersData);
    localStorage.setItem("userDetailsV2", JSON.stringify(existingUsersData));
  } catch (error) {
    dispatch({
      type: USER_DETAILS_FAIL,
      payload: {},
    });
  }
};

export const updateUser = (userData) => async (dispatch, getState) => {
  dispatch({ type: USER_DETAILS_UPDATE_REQUEST, payload: userData });
  const {
    userDetails: { userDetailsV2 },
  } = getState();
  try {
    dispatch({ type: USER_DETAILS_UPDATE_SUCCESS, payload: userData });
    // dispatch({ type: USER_SIGNIN_SUCCESS, payload: data });
    localStorage.setItem("userDetailsV2", JSON.stringify(userData));
  } catch (error) {
    const message =
      error.response && error.response.data.message
        ? error.response.data.message
        : error.message;
    dispatch({ type: USER_UPDATE_PROFILE_FAIL, payload: message });
  }
};
