import {
  USER_DETAILS_FAIL,
  USER_DETAILS_REQUEST,
  USER_DETAILS_SUCCESS,
} from "../constants/userConstants";

export const details = (userData) => async (dispatch) => {
  dispatch({
    type: USER_DETAILS_REQUEST,
    payload: userData,
  });
  try {
    dispatch({ type: USER_DETAILS_SUCCESS, payload: userData });

    const userObj = {};
    userObj[userData["userId"]] = userData;
    console.log("userObj ----> ", userObj);
    let existingUsersData = localStorage.getItem("userDetails");
    if (!existingUsersData) {
      existingUsersData = {};
    }
    console.log("existingUsersData ---> ", existingUsersData);
    existingUsersData[userObj["userId"]] = userObj;
    console.log("updatedObject ---> ", existingUsersData);
    localStorage.setItem("userDetails", JSON.stringify(existingUsersData));
  } catch (error) {
    dispatch({
      type: USER_DETAILS_FAIL,
      payload: {},
    });
  }
};
